sp_widget.script:
(function() {
	if (input.keywords != null && input.keywords != '')
		data.items = getCatalogItems(input.keywords);

	function getCatalogItems(keywords) {
	  var incident = new GlideRecord("incident");
        incident.addQuery("state", 1);
        incident.setLimit(10);
        incident.query();

		var results = [];
			  while (incident.next()) {
         $sp.getRecordDisplayValues(item, incident, 'number,caller,category,priority','assignment_group','short_description');
        }

			var item = {};

			results.push(item);
		}
		return results;
})();
