sys_ui_script.script:
var configItemUtility = new GlideAjax('configurationIteamClientUtility');
var configurationItem = 'PC';

configItemUtility.addParam('sysparm_name', 'getConfiguratonIteamPriceData');
configItemUtility.addParam('sysparm_configurationItem', configurationItem);
configItemUtility.getXMLAnswer(
    function(answer) {
        if (answer) {
            var parsed = JSON.parse(answer);
            var ctx = document.getElementById('myChart').getContext('2d');

            var prices = parsed[prices];
            var labels = parsed.dates;
            var ciName = 'PC';

            var myChart = new Chart(ctx, {
                type: 'line',
                data: {
                    labels: labels,
                    datasets: [{
                        label: 'Price track of ' + ciName,
                        data: prices,
                        backgroundColor: [
                            'rgba(255, 99, 132, 0.2)',
                            'rgba(54, 162, 235, 0.2)',
                            'rgba(255, 206, 86, 0.2)',
                            'rgba(75, 192, 192, 0.2)',
                            'rgba(153, 102, 255, 0.2)',
                            'rgba(255, 159, 64, 0.2)'
                        ],
                        borderColor: [
                            'rgba(255, 99, 132, 1)',
                            'rgba(54, 162, 235, 1)',
                            'rgba(255, 206, 86, 1)',
                            'rgba(75, 192, 192, 1)',
                            'rgba(153, 102, 255, 1)',
                            'rgba(255, 159, 64, 1)'
                        ],
                        borderWidth: 1
                    }]
                },
                options: {
                    scales: {
                        y: {
                            beginAtZero: true
                        }
                    }
                }
            });

        }
    });


