sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue === '') {
        return;
    }

    var incidentUtility = new GlideAjax('incidentClientUtility');

    // assignment_group.nill() in Business rule
    var assignmentGroup = g_form.getValue('assignment_group');
    if (assignmentGroup == '') {
        g_form.clearValue('assigned_to');

        incidentUtility.addParam('sysparm_name', 'getErrorMessage');
        incidentUtility.getXMLAnswer(function(answer) {
            g_form.showErrorBox('assigned_to', answer);
        });
    } else {
        incidentUtility.addParam('sysparm_name', 'checkIfMembers');
        incidentUtility.addParam('sysparm_user_name', assignmentGroup);
        incidentUtility.getXML(result);
    }

    function result(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');
        if (!answer) {
            g_form.clearValue('assigned_to');
            g_form.showErrorBox('assigned_to', 'Cannot set when the group is empty!');
        }
    }
}
