sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    try {

        var configurationItem = g_form.getValue('cmdb_ci'),
            configItemUtility = new GlideAjax('changeClientUtility');

        configItemUtility.addParam('sysparm_name', 'copyData');
        configItemUtility.addParam('sysparm_configurationItem', configurationItem);
        configItemUtility.getXMLAnswer(
            function(answer) {
                if (answer) {
                    var parstAnswer = JSON.parse(answer);
                    var result =  '\n' + 'Configuration item ' + '\n' +
                        'Name: ' + parstAnswer['name'] + '\n' +
                        'Asset tag: ' + parstAnswer['assetTag'] + '\n' +
                        'Manufacturer: ' + parstAnswer.manufacturer + '\n' +
                        'Company: ' + parstAnswer.company;

					var description = g_form.getValue('description');
                    g_form.setValue('description', description + result);
                } else {
                    g_form.addInfoMessage('No new data');
                }
            }
        );

    } catch (err) {
        g_form.addErrorMessage(err);
    }
}
