sys_script_client.script:
function onLoad() {

    var parent = g_form.getUniqueValue();

    var incidentUtility = new GlideAjax('incidentClientUtility');

    incidentUtility.addParam('sysparm_name', 'checkForChild');
    incidentUtility.addParam('sysparm_parentIncident', parent);

    incidentUtility.getXMLAnswer(
        function(childIncidents) {
            if (childIncidents) {
				// due the answer shall be arr with all the incidens numbers
                var incidents = childIncidents.split(',');
                g_form.addInfoMessage('There are ' + incidents.length + ' opened incidents: ' + incidents);
            } else {
                g_form.addErrorMessage('There are NO open incidents.');
            }
        }
    );
}
