sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    var parent = current.getUniqueValue();
    var emailScript = new emailUtility();
    var data = JSON.parse(emailScript.getIncidentChildData(parent));

    var count = data.length;
    var messagesParent = '<div class="info" style="background-color: #e7f3fe; border-left: 6px solid #2196F3; margin-bottom: 15px; padding: 4px 12px;">' +
        '<p>This incident is parent of:<strong>' + count + '</strong>incidents</p></div>';

    if (count > 0) {
        template.print(messagesParent);

        for (var i = 0; i < count; i++) {
            template.print('<div class="success" style="background-color: #ddffdd; border-left: 6px solid #04AA6D; margin-bottom: 15px; padding: 4px 12px;">' + '<p><strong>Number: ' + data[i].number + '-</strong>' + '</p><p><strong>Short Description: ' + data[i].description + '-</strong>' + '</p></div>');
            template.print('<br /> <br />');

            email.addAddress('cc', data[i].email, data[i].caller);
        }
    } else {
        template.print('<div class="info" style="background-color: #e7f3fe; border-left: 6px solid #2196F3; margin-bottom: 15px; padding: 4px 12px;">' +
            '<p>This incident is <strong>NOT parent</strong> of any incidents!</p></div>');
    }

    //     var emails = [];
    //  for (var person = 0; person < count; person++) {
    //      var emaill = emailScript.getEmail(data.childCallers[person]);
    //      emails.push(emaill);

    //      email.addAddress('cc', emails[person], data.childuserNames[person]);

    //  }

})(current, template, email, email_action, event);
