sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {
// TO ASK IF I NEED TO ADD HERE THE HTML AND CSS?
    var reportUtility = new serviceDeskReport(),
        titleReport = reportUtility.getMessageReportTitle(),
        queriedIncidentsCount = reportUtility.getCount(),
        forPrintIncidents = reportUtility.getPrint(),
        emails = reportUtility.getGroupMemberMails();

    template.print(titleReport + '<br></br>');
    template.print(queriedIncidentsCount + '<br></br>');
    template.print(forPrintIncidents.join('<br></br>'));

    var count = (emails.length);
    if (count > 0) {
        for (var i = 0; i < count; i++) {
            email.addAddress("cc", emails[count]);
        }
    }

})(current, template, email, email_action, event);
