sys_script_include.script:
var serviceDeskReport = Class.create();
serviceDeskReport.prototype = {
   initialize: function() {
        this.groupSys_id = 'd625dccec0a8016700a222a0f7900d06';
        this.titleReport = 'SERVICE DESK WEEKLY REPORT';
        this.groupName = 'SERVICE DESK';
        this.count = 0;
        this.print = [];
        this.groupMemebersMails = [];
        this._queryIncident();
        this._getMails();
    },

    _queryIncident: function() {
        var incident = new GlideRecord('incident');
        incident.addQuery('assignment_group', this.groupSys_id);

        var qc = incident.addQuery('state', 1);
        qc.addOrCondition('state',  2);
        incident.orderBy('priority');
        incident.query();

        while (incident.next()) {
            this.print.push('{' + incident.getValue('number') + '} –' + '{' + incident.getDisplayValue('priority') + '} – {' + incident.getValue('short_description') + '}');
        }

        this.count = this.print.length;
    },

    _getMails: function() {
        var serviceDeskMembers = new GlideRecord("sys_user_grmember");
        serviceDeskMembers.addQuery('group',  this.groupSys_id);
        serviceDeskMembers.query();

        while (serviceDeskMembers.next()) {
            this.groupMemebersMails.push(serviceDeskMembers.user.email.toString());
        }
    },

    getMessageReportTitle: function() {
        return this.titleReport;
    },

    getCount: function() {
        return this.count;
    },

    getPrint: function() {
        return this.print;
    },

    getGroupMemberMails: function() {
        return this.groupMemebersMails;
    },

    type: 'serviceDeskReport'
};
