sys_script_include.script:
var configurationIteamClientUtility = Class.create();
configurationIteamClientUtility.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getConfiguratonIteamPriceData: function() {
        var prices = [];
        var dates = [];
		var ciName = this.getParameter('sysparm_configurationItem');

        var ci = new GlideRecord('cmdb_ci');
        ci.addQuery('name', ciName);
        ci.query();

        if (ci.next()) {
            var data = ci.getValue('x_847697_serviceno_u_price_track');
            var all = data.split(',');
            var length = data.split(',').length - 1;

            for (var i = 0; i < length; i++) {
                prices.push(all[i].split(' ')[1]);
                dates.push(all[i].split(' ')[2]);
            }
        }

        var rawData = {};
        rawData.dates = dates;
        rawData.prices = prices;

        var dataStr = JSON.stringify(rawData);

		return dataStr;
    },

    type: 'configurationIteamClientUtility'
});
