sys_script_include.script:
var changeClientUtility = Class.create();
changeClientUtility.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    /**
    * This function creates an object based on the paramters received (sysparm_configurationItem) and copy the fields(name, asset_tag, manufacturer, company) from the Configuration iteam.
    *
    * @param            {String}         sysparm_configurationItem          contains the sys_id of the Configuration iteam

    * @returns          {obj}            dataObj                            the newly created obj {'name': , 'asset_tag': , 'manufacturer': , 'company': }
    */

    copyData: function() {
        var configItem = this.getParameter('sysparm_configurationItem');
        var configurationIteamGR = new GlideRecord("cmdb_ci");
        //         configurationIteamGR.addQuery("sys_id", configItem);
        //         configurationIteamGR.query();

        var dataObj = {};

        if (configurationIteamGR.get(configItem)) {
            dataObj.name = this._getFieldValue('name', configurationIteamGR);
            dataObj.assetTag = this._getFieldValue('asset_tag', configurationIteamGR);
            dataObj.manufacturer = this._getFieldValue('manufacturer', configurationIteamGR);
            dataObj.company = this._getFieldValue('company', configurationIteamGR);
        }

        return JSON.stringify(dataObj);
    },

    /**
    * This function checks if a field of a Configuration iteam is empty based on the paramters received (fieldName, configurationIteamGR).
    *
    * @param            {String}            fieldName                     contains name for the checked field
    * @param            {GlideRecord}       configurationIteamGR          contains GlideRecord for the checked Configuration team

    * @returns          {String}            If the field is empty =>  "N/A", else returns => the field value of the configuration iteam
    */

    _getFieldValue: function(fieldName, configurationIteamGR) {
        if (configurationIteamGR[fieldName].nil()) {
            return "N/A";
        }

        return configurationIteamGR.getDisplayValue(fieldName);
        // configurationIteamGR[fieldName].getDisplayValue();
    },

    type: 'changeClientUtility'
});
