sys_script_include.script:
var incidentUtility = Class.create();
incidentUtility.prototype = {

    /**
    * This function creates a query based on the paramters received(parentIncident) and updates the excisting child with the same State, Resolution code and Resolution notes as the parent Incident.
    *
    * @param     {obj}     parent contains the the Incident instance.

    */

    updateChild: function(parentIncident) {
        var parentID = parentIncident.getValue('parent_incident'),
            parentState = parentIncident.getValue('state'),
            parentCloseCode = parentIncident.getValue('close_code'),
            parentCloseNote = parentIncident.getValue('close_notes');

        var incidentChildGR = new GlideRecord('incident');
        incidentChildGR.addEncodedQuery('parent_incident.sys_id=' + parentID);
        incidentChildGR.query();

        while (incidentChildGR.next()) {
            incidentChildGR.state = parentState;
            incidentChildGR.close_code = parentCloseCode;
            incidentChildGR.close_notes = parentCloseNote;

            incidentChildGR.update();
        }
    },

    type: 'incidentUtility'
};
